import{_ as p,c as t,o as e,ae as r}from"./chunks/framework.DjAKN8N1.js";const m=JSON.parse('{"title":"数与式的化简求值 p2","description":"","frontmatter":{},"headers":[],"relativePath":"math/p2.md","filePath":"math/p2.md","lastUpdated":1749477929000}'),o={name:"math/p2.md"};function c(i,a,s,n,$,l){return e(),t("div",null,a[0]||(a[0]=[r('<h1 id="数与式的化简求值-p2" tabindex="-1">数与式的化简求值 p2 <a class="header-anchor" href="#数与式的化简求值-p2" aria-label="Permalink to &quot;数与式的化简求值 p2&quot;">​</a></h1><p><strong>数与式-基础：大题第一题:数与式的化简求值稳定得分</strong></p><h2 id="需要知道的知识" tabindex="-1">需要知道的知识 <a class="header-anchor" href="#需要知道的知识" aria-label="Permalink to &quot;需要知道的知识&quot;">​</a></h2><p>0次：$x^0=1(x\\neq 0)$</p><p>负次：$a^{-a} = \\frac{1}{x^a}$</p><p>绝对值：$|x|=\\begin{cases} x,,,x\\geq0\\ -x,,,x&lt;0\\end{cases}$</p><p>根号： $$ \\sqrt[5]{a}=x $$</p><p>$$ a=x^5 $$</p><p>平方差公式：$a^2-b^2=(a+b)(a-b)$</p><p>完全平方公式：$(a+b)^2=a^2+2ab+b^2$、 $(a-b)^2=a^2-2ab+b^2$</p><p>除以一个数等于乘这个数的相反数：$b\\div a=b\\cdot \\frac{1}{a}$</p><p>三角函数：记特殊角的值</p><p><img src="https://p2.myzwq.com/i/PicGo/202301301243739.png" alt=""></p><p>通分：$a=\\frac{a^2}{a}$（在代数式化简里好用）</p><h2 id="注意" tabindex="-1">注意 <a class="header-anchor" href="#注意" aria-label="Permalink to &quot;注意&quot;">​</a></h2><p>$0^0$ 不存在，无意义</p><h2 id="黑板" tabindex="-1">黑板 <a class="header-anchor" href="#黑板" aria-label="Permalink to &quot;黑板&quot;">​</a></h2><p><img src="https://p2.myzwq.com/i/PicGo/202301301246147.png" alt=""></p><h2 id="题" tabindex="-1">题 <a class="header-anchor" href="#题" aria-label="Permalink to &quot;题&quot;">​</a></h2><p>直接化简求值：</p><p><img src="https://p2.myzwq.com/i/PicGo/202301301249072.png" alt="">=$3$</p><p><img src="https://p2.myzwq.com/i/PicGo/202301301251757.png" alt="">=$2\\sqrt{3}$</p><p>代数式的化简求值（先化简再求值）：</p><p><img src="https://p2.myzwq.com/i/PicGo/202301301307290.png" alt="">=$\\frac{1}{a+3}=\\frac{1}{4}$</p><p><img src="https://p2.myzwq.com/i/PicGo/202301301309905.png" alt="">=$\\frac{1}{a-b}=\\frac{\\sqrt{3}}{3}$</p>',25)]))}const d=p(o,[["render",c]]);export{m as __pageData,d as default};
